const SEND_ANN = 'SEND_ANN';


const initialState = {
    announcements: [
        {
            title: '',
            description: '',
        },
    ],
};

const annReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_ANN:
            return {
                ...state,
                title:'',
                description:'',
                announcements: [...state.announcements, { title: action.values.title, description: action.values.description}]
            }
    }
    return state;
}

export const setAnn = (values) => {
    return {
        type: SEND_ANN, values
    }
}

export default annReducer;
