import React, {useState} from 'react';
import Ann from "./ann";
import {setAnn} from "../../redux/ann-reducer";

const AnnSwitch = (props) => {

    let [editMode, setEditMode] = useState(false);

    return (
        <div>
            {editMode
                ? <Ann initial={props.initial} setAnn={props.setAnn}/>
                : <AnnDisplay goToEditMode={() => {setEditMode(true)
                }} initial={props.initial} />}
        </div>
    );
};

const AnnDisplay = (props) => {

    return <div>
        {<div><button onClick={props.goToEditMode}>edit</button></div>}
        {props.initial.map((p, idx)=>
        <div key={idx}>
            <div>{p.title}</div>
            <div>{p.description}</div>
        </div>
        )};
    </div>
}


export default AnnSwitch;
