const SEND_ANNOUNCEMENT = 'SEND_ANNOUNCEMENT';


let initialState = {
    title:' ',
    description:' ',
};

const announcementReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_ANNOUNCEMENT:
            return {
                ...state,
                title: action.title,
            }
    }
    return state;
}

export const setAnnouncement = (title) => {
    return {
        type: SEND_ANNOUNCEMENT, title
    }
}

export default announcementReducer;
