const SEND_ANN = 'SEND_ANN';

const initialState = {
    announcements: [],
};

const annReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_ANN:
            return {...state,
                announcements: [...state.announcements,  { title: action.values.initial.title, description: action.values.initial.description}]}
    }
    return state;
}

export const setAnn = (values) => {
    debugger
    return {
        type: SEND_ANN, values
    }
}

export default annReducer;
