import React from 'react';
import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik';
import PostAnnoun from "../announcement/PostAnnoun";
import {setAnn} from "../../redux/ann-reducer";

const initialValues = {
    initial: [
        {
            title: '',
            description: '',
        },
    ],
};

const Ann = (props) => {
debugger
    /*const announcementElements =
        props.initial.map((p, idx)=><div key={idx}>
                <div>{p.title}</div>
                <div>{p.description}</div>
            </div>
        );*/
return(

    <div>
        <h1>Invite friends</h1>
        <Formik
            initialValues={initialValues}

            onSubmit={ (values) => {
                console.log(JSON.stringify(values, ))
                props.setAnn(values);
                props.onSubmit();

            }}

        >
            {({ values }) => (
                <Form>
                    <FieldArray name="initial">
                        {({ insert, remove, push }) => (
                            <div>
                                {values.initial.length > 0 &&
                                    values.initial.map((f, index) => (
                                        <div className="row" key={index}>


                                            <div className="col">
                                                <label htmlFor={`initial.${index}.title`}>Name</label>
                                                <Field
                                                    name={`initial.${index}.title`}
                                                    placeholder="title"
                                                    type="text"
                                                />
                                                <ErrorMessage
                                                    name={`initial.${index}.title`}
                                                    component="div"
                                                    className="field-error"
                                                />
                                            </div>


                                            <div className="col">
                                                <label htmlFor={`initial.${index}.description`}>description</label>
                                                <Field
                                                    name={`initial.${index}.description`}
                                                    placeholder="description"
                                                    type="text"
                                                />
                                                <ErrorMessage
                                                    name={`initial.${index}.title`}
                                                    component="div"
                                                    className="field-error"
                                                />
                                            </div>


                                            <div className="col">
                                                <button
                                                    type="button"
                                                    className="secondary"
                                                    onClick={() => remove(index)}
                                                >
                                                    X
                                                </button>
                                            </div>
                                        </div>
                                    ))}


                                <button
                                    type="button"
                                    className="secondary"
                                    onClick={() => push({ title: '', description: '' })}
                                >
                                    Add Friend
                                </button>


                            </div>
                        )}
                    </FieldArray>

                    <button   type="submit">Invite</button>
                </Form>
            )}
        </Formik>
        <div>
            {/*{ announcementElements}*/}
        </div>
    </div>
)};



export default Ann;
