import React from 'react';
import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik';
import classes from "./ann.module.css";

const Ann = (props) => {

    const initialValues = {
        initial: props.initial
    };

return(
    <div className={classes.annWrapper}>
        <h1 className={classes.hTitle}>Announcement add</h1>

        <Formik
            initialValues={initialValues}

            onSubmit={ (values) => {
               // console.log(JSON.stringify(values, ))
                props.setAnn(values);
                props.goToEditMode();
            }}>
            {({ values }) => (
                <Form>
                    <FieldArray name="initial">
                        {({ insert, remove, push }) => (
                            <div>
                                {values.initial.length > 0 &&
                                    values.initial.map((f, index) => (
                                        <div className="row" key={index}>

                                            <div className="col">
                                                <label htmlFor={`initial.${index}.title`}><span className={classes.title}>Title</span></label>
                                                <Field
                                                    name={`initial.${index}.title`}
                                                    placeholder="title"
                                                    type="text"
                                                />
                                                <ErrorMessage
                                                    name={`initial.${index}.title`}
                                                    component="div"
                                                    className="field-error"
                                                />
                                            </div>

                                            <div className="col">
                                                <label htmlFor={`initial.${index}.description`}><span className={classes.description}>Description</span></label>
                                                <Field
                                                    name={`initial.${index}.description`}
                                                    placeholder="description"
                                                    type="text"
                                                />
                                                <ErrorMessage
                                                    name={`initial.${index}.title`}
                                                    component="div"
                                                    className="field-error"
                                                />
                                            </div>

                                            <div className="col">
                                                <button
                                                    type="button"
                                                    className="secondary"
                                                    onClick={() => remove(index)}
                                                >
                                                    X
                                                </button>
                                            </div>
                                        </div>
                                    ))}

                                <button
                                    type="button"
                                    className="secondary"
                                    onClick={() => push({ title: '', description: '' })}
                                >
                                    Add Friend
                                </button>

                            </div>
                        )}
                    </FieldArray>
                    <button type="submit">Invite</button>
                    <button type="reset">Reset</button>
                </Form>
            )}
        </Formik>
    </div>
)};

export default Ann;
