import React from 'react';
import ReactDOM from 'react-dom';
import { Formik, Field, Form } from 'formik';
import PostAnnoun from "./PostAnnoun";

const Announcement = (props) => {

    let announcementElements =
        props.announcement.map(p => <PostAnnoun  title={p.title} description={p.description}/>);

    return (
        <div>
            <h1>Sign Up</h1>
            <Formik
                initialValues={{
                    title: '',
                    description: '',
                }}

                onSubmit={(values) => {
                    props.setAnnouncement(values);
                }}
            >
                <Form>
                    <label htmlFor="title"> title </label>
                    <Field id="title" name="title" placeholder="Enter text"/>

                    <label htmlFor="description">Description</label>
                    <Field id="description" name="description" placeholder="Enter text"/>

                    <button type="submit">Submit</button>
                </Form>
            </Formik>
            <div>
                <b>result announcement:</b> {props.title}
            </div>
            <div>
                {announcementElements}
            </div>
            <div>
                <b>result description:</b> {props.description}
            </div>
        </div>);
};
export default Announcement;
