import Announcement from "./announcement";
import React from 'react';
import {connect} from "react-redux";
import {compose} from "redux";
import {setAnnouncement} from "../redux/announcement-reducer";

class AnnouncementContainer extends React.Component {

    render() {
        return <>
            <Announcement title={this.props.title}
            />
        </>
    }
}

let mapStateToProps = (state) => {
    return {
        title: state.announcementPage.title,

    }
}

export default compose(
    connect(mapStateToProps, {setAnnouncement}),
)(Announcement)
