import React from 'react';
import ReactDOM from 'react-dom';
import { Formik, Field, Form } from 'formik';

const Announcement = (props) => (
    <div>
        <h1>Sign Up</h1>
        <Formik
            initialValues={{
                announcement: '',
                description: '',
            }}
            onSubmit={ (values) => {
                alert(JSON.stringify(values, null,2));
                props.setAnnouncement(values)
            }}
        >
            <Form>
                <label htmlFor="announcement"> Announcement </label>
                <Field id="announcement" name="announcement" placeholder="Enter text" />

                <label htmlFor="description">Description</label>
                <Field id="description" name="description" placeholder="Enter text" />

                <button type="submit">Submit</button>
            </Form>
        </Formik>
        <div>
            result announcement: {props.announcement}

            <b>result description: {props.description}</b>
        </div>
    </div>
);
export default Announcement;
