import React from 'react';
import { Formik, Field, Form, ErrorMessage, FieldArray } from 'formik';

const initialValues = {
    announcements: [
        {
            title: '',
            description: '',
        },
    ],
};

const Ann = (props) => (
    <div>
        <h1>Invite friends</h1>
        <Formik
            initialValues={initialValues}
            onSubmit={ (values) => {
                alert(JSON.stringify(values, ));
            }}
        >
            {({ values }) => (
                <Form>
                    <FieldArray name="announcements">
                        {({ insert, remove, push }) => (
                            <div>
                                {values.announcements.length > 0 &&
                                    values.announcements.map((f, index) => (
                                        <div className="row" key={index}>


                                            <div className="col">
                                                <label htmlFor={`announcements.${index}.title`}>Name</label>
                                                <Field
                                                    name={`announcements.${index}.title`}
                                                    placeholder="title"
                                                    type="text"
                                                />
                                                <ErrorMessage
                                                    name={`announcements.${index}.title`}
                                                    component="div"
                                                    className="field-error"
                                                />
                                            </div>


                                            <div className="col">
                                                <label htmlFor={`announcements.${index}.description`}>description</label>
                                                <Field
                                                    name={`announcements.${index}.description`}
                                                    placeholder="description"
                                                    type="text"
                                                />
                                                <ErrorMessage
                                                    name={`announcements.${index}.title`}
                                                    component="div"
                                                    className="field-error"
                                                />
                                            </div>


                                            <div className="col">
                                                <button
                                                    type="button"
                                                    className="secondary"
                                                    onClick={() => remove(index)}
                                                >
                                                    X
                                                </button>
                                            </div>
                                        </div>
                                    ))}


                                <button
                                    type="button"
                                    className="secondary"
                                    onClick={() => push({ title: '', description: '' })}
                                >
                                    Add Friend
                                </button>


                            </div>
                        )}
                    </FieldArray>

                    <button type="submit">Invite</button>
                </Form>
            )}
        </Formik>
    </div>
);
export default Ann;
